find:function(b) {
  if ("s" < typeof b) {
    var a = [];
    __ITERATE__(this.e, function(d) {
      a = a.concat($(b, d).e);
    });
    return $(a.filter(function(a, b, c) {
      return c.indexOf(a) == b;
    }));
  }
  var e = this.e, a = $(b).e.filter(function(a) {
    return e.some(function(b) {
      for (z = a;z && (z = z.parentNode);) {
        if (z === b) {
          return !0;
        }
      }
    });
  });
  return $(a);
}

